 63	
 64	 @PostMapping("/{employeeId}/primary-education-child")
 65	 public ResponseEntity<?> addPrimaryEducationChild(
 66	 @PathVariable Long employeeId,
 67	 @RequestParam("childFullName") String childFullName,
 68	 @RequestParam("childBirthDate") String childBirthDate,
 69	 @RequestParam("academicYear") String academicYear,
 70	 @RequestParam("pdfFile") MultipartFile pdfFile) {
 71	 
 72	 try {
 73	 Optional<Employee> employeeOpt = employeeService.findById(employeeId);
 74	 if (!employeeOpt.isPresent()) {
 75	 return ResponseEntity.notFound().build();
 76	 }
 77	
 78	 Employee employee = employeeOpt.get();
 79	 PrimaryEducationChild child = new PrimaryEducationChild();
 80	 child.setChildFullName(childFullName);
 81	 child.setChildBirthDate(LocalDate.parse(childBirthDate));
 82	 child.setAcademicYear(academicYear);
 83	 child.setEmployee(employee);
 84	
 85	 // Store PDF file
 86	 if (!pdfFile.isEmpty()) {
 87	 String filePath = fileStorageService.storeFile(pdfFile, "primary-education");
 88	 child.setPdfFilePath(filePath);
 89	 child.setPdfFileName(pdfFile.getOriginalFilename());
 90	 }
 91	
 92	 employee.getPrimaryEducationChildren().add(child);
 93	 Employee updatedEmployee = employeeService.updateEmployee(employee);
 94	 
 95	 return ResponseEntity.ok(updatedEmployee);
 96	 } catch (Exception e) {
 97	 return ResponseEntity.status(HttpStatus.INTERNAL_SERVER_ERROR)
 98	 .body("Error adding primary education child: " + e.getMessage());
 99	 }
 100	 }
 101	
