 15	@Service
 16	@Transactional
 17	public class ApplicationServiceImpl implements ApplicationService {
 18	
 19	 @Autowired
 20	 private ApplicationRepository applicationRepository;
 21	
 22	 @Autowired
 23	 private EmployeeRepository employeeRepository;
 24	
 25	 @Override
 26	 public Application submitApplication(Long employeeId) {
 27	 Optional<Employee> employeeOpt = employeeRepository.findById(employeeId);
 28	 if (employeeOpt.isPresent()) {
 29	 Application application = new Application(employeeOpt.get());
 30	 return applicationRepository.save(application);
 31	 }
 32	 throw new RuntimeException("Employee not found with id: " + employeeId);
 33	 }
 34	
 35	 @Override
 36	 public Optional<Application> findById(Long id) {
 37	 return applicationRepository.findById(id);
 38	 }
...
 54	
 55	 @Override
 56	 public Application approveApplication(Long applicationId, String reviewedBy) {
 57	 Optional<Application> applicationOpt = applicationRepository.findById(applicationId);
 58	 if (applicationOpt.isPresent()) {
 59	 Application application = applicationOpt.get();
 60	 application.setStatus(Application.ApplicationStatus.ACCEPTED);
 61	 application.setReviewDate(LocalDateTime.now());
 62	 application.setReviewedBy(reviewedBy);
 63	 application.setRejectionReason(null); // Clear any previous rejection reason
 64	 return applicationRepository.save(application);
 65	 }
 66	 throw new RuntimeException("Application not found with id: " + applicationId);
 67	 }
 68	
 69	 @Override
 70	 public Application rejectApplication(Long applicationId, String rejectionReason, String reviewedBy) {
 71	 Optional<Application> applicationOpt = applicationRepository.findById(applicationId);
 72	 if (applicationOpt.isPresent()) {
 73	 Application application = applicationOpt.get();
 74	 application.setStatus(Application.ApplicationStatus.REJECTED);
 75	 application.setReviewDate(LocalDateTime.now());
 76	 application.setRejectionReason(rejectionReason);
 77	 application.setReviewedBy(reviewedBy);
 78	 return applicationRepository.save(application);
 79	 }
 80	 throw new RuntimeException("Application not found with id: " + applicationId);
 81	 }
 82	
 83	 @Override
 84	 public void deleteApplication(Long id) {
 85	 applicationRepository.deleteById(id);
 86	 }
 87	}
