 34	 ngOnInit() {
 35	 this.route.params.subscribe(params => {
 36	 this.matricule = params['matricule'];
 37	 if (this.matricule) {
 38	 this.loadEmployeeData();
 39	 }
 40	 });
 41	 }
 42	
 43	 loadEmployeeData() {
 44	 this.loading = true;
 45	 this.error = '';
 46	
 47	 this.employeeService.getEmployeeByMatricule(this.matricule).subscribe({
 48	 next: (employee) => {
 49	 this.employee = employee;
 50	 if (employee.id) {
 51	 this.loadApplications(employee.id);
 52	 }
 53	 },
 54	 error: (error) => {
 55	 this.error = 'Employee not found. Please check your matricule number.';
 56	 this.loading = false;
 57	 }
 58	 });
 59	 }
 60	
 61	 loadApplications(employeeId: number) {
 62	 this.employeeService.getEmployeeApplications(employeeId).subscribe({
 63	 next: (applications) => {
 64	 this.applications = applications;
 65	 this.loading = false;
 66	 },
 67	 error: (error) => {
 68	 this.error = 'Error loading applications. Please try again.';
 69	 this.loading = false;
 70	 }
 71	 });
 72	 }
 73	
 74	 // PDF viewing methods
 75	 viewPDF(pdfPath: string, pdfName: string) {
