 91	 case ApplicationStatus.EN_ATTENTE:
 92	 return 'status-pending';
 93	 case ApplicationStatus.ACCEPTED:
 94	 return 'status-accepted';
 95	 case ApplicationStatus.REJECTED:
 96	 return 'status-rejected';
 97	 default:
 98	 return '';
 99	 }
