 173	 formData.append('childBirthDate', child.childBirthDate);
 174	 formData.append('academicYear', child.academicYear);
 175	 if (file && file.size > 0) {
 176	 formData.append('pdfFile', file);
 177	 }
 178	
 179	 await this.employeeService.addHigherEducationChild(registeredEmployee.id, formData).toPromise();
 180	 }
 181	 }
 182	
 183	 // Submit application
 184	 await this.employeeService.submitApplication(registeredEmployee.id).toPromise();
 185	
 186	 this.message = 'Employee registered and application submitted successfully!';
 187	 this.messageType = 'success';
 188	
 189	 // Redirect to user dashboard after 2 seconds
 190	 setTimeout(() => {
 191	 this.router.navigate(['/user-dashboard', this.employee.matricule]);
 192	 }, 2000);
 193	
 194	 } catch (error: any) {
 195	 console.error('Registration error:', error);
 196	
 197	 let errorMessage = 'Unknown error occurred';
 198	
 199	 if (error.error) {
 200	 if (typeof error.error === 'string') {
 201	 errorMessage = error.error;
 202	 } else if (error.error.message) {
 203	 errorMessage = error.error.message;
 204	 } else {
 205	 errorMessage = JSON.stringify(error.error);
 206	 }
 207	 } else if (error.message) {
 208	 errorMessage = error.message;
 209	 }
 210	
 211	 this.message = 'Error: ' + errorMessage;
 212	 this.messageType = 'error';
 213	 } finally {
 214	 this.isSubmitting = false;
 215	 }
 216	 }
 217	
 218	 goToUserDashboard() {
 219	 if (this.employee.matricule) {
 220	 this.router.navigate(['/user-dashboard', this.employee.matricule]);
 221	 }
 222	 }
 223	
 224	 goToAdminDashboard() {
 225	 this.router.navigate(['/admin-dashboard']);
 226	 }
 227	}
