 122	 errorResponse.put("error", "File not found");
 123	 return ResponseEntity.notFound().build();
 124	 }
 125	 } catch (Exception e) {
 126	 Map<String, String> errorResponse = new HashMap<>();
 127	 errorResponse.put("error", "Could not delete file: " + e.getMessage());
 128	 return ResponseEntity.badRequest().body(errorResponse);
 129	 }
 130	 }
 131	
 132	 @GetMapping("/info/{fileName:.+}")
 133	 public ResponseEntity<?> getFileInfo(@PathVariable String fileName) {
 134	 try {
 135	 Resource resource = fileStorageService.loadFileAsResource(fileName);
 136	 
 137	 Map<String, Object> fileInfo = new HashMap<>();
 138	 fileInfo.put("fileName", resource.getFilename());
 139	 fileInfo.put("size", resource.getFile().length());
 140	 fileInfo.put("lastModified", resource.getFile().lastModified());
 141	 fileInfo.put("exists", resource.exists());
 142	 fileInfo.put("readable", resource.isReadable());
 143	 
