 145	 } catch (Exception e) {
 146	 Map<String, String> errorResponse = new HashMap<>();
 147	 errorResponse.put("error", "Could not get file info: " + e.getMessage());
 148	 return ResponseEntity.badRequest().body(errorResponse);
 149	 }
