Path: backend/src/main/java/com/example/employeemanagement/service/FileStorageServiceImpl.java
 1	package com.example.employeemanagement.service;
 2	
 3	import org.springframework.beans.factory.annotation.Value;
 4	import org.springframework.stereotype.Service;
 5	import org.springframework.web.multipart.MultipartFile;
 6	
 7	import java.io.IOException;
 8	import java.nio.file.Files;
 9	import java.nio.file.Path;
 10	import java.nio.file.Paths;
 11	import java.nio.file.StandardCopyOption;
 12	import java.util.UUID;
 13	
 14	@Service
 15	public class FileStorageServiceImpl implements FileStorageService {
 16	
 17	 @Value("${file.upload-dir:uploads}")
 18	 private String uploadDir;
 19	
 20	 @Override
 21	 public String storeFile(MultipartFile file, String directory) throws IOException {
 22	 // Create directory if it doesn't exist
 23	 Path uploadPath = Paths.get(uploadDir, directory);
 24	 if (!Files.exists(uploadPath)) {
 25	 Files.createDirectories(uploadPath);
 26	 }
 27	
 28	 // Generate unique filename
 29	 String originalFilename = file.getOriginalFilename();
 30	 String fileExtension = "";
 31	 if (originalFilename != null && originalFilename.contains(".")) {
 32	 fileExtension = originalFilename.substring(originalFilename.lastIndexOf("."));
 33	 }
 34	 String uniqueFilename = UUID.randomUUID().toString() + fileExtension;
 35	
 36	 // Store file
 37	 Path filePath = uploadPath.resolve(uniqueFilename);
 38	 Files.copy(file.getInputStream(), filePath, StandardCopyOption.REPLACE_EXISTING);
 39	
 40	 return filePath.toString();
 41	 }
 42	
 43	 @Override
 44	 public byte[] loadFile(String filePath) throws IOException {
 45	 Path path = Paths.get(filePath);
 46	 return Files.readAllBytes(path);
 47	 }
 48	
 49	 @Override
 50	 public void deleteFile(String filePath) throws IOException {
 51	 Path path = Paths.get(filePath);
 52	 Files.deleteIfExists(path);
 53	 }
 54	
 55	 @Override
 56	 public boolean fileExists(String filePath) {
 57	 Path path = Paths.get(filePath);
 58	 return Files.exists(path);
 59	 }
 60	}
