 15	export class UserDashboardComponent implements OnInit {
 16	 employee: Employee | null = null;
 17	 applications: Application[] = [];
 18	 loading = true;
 19	 error = '';
 20	 matricule = '';
 21	
 22	 // For PDF viewer modal
 23	 showPDFModal = false;
 24	 currentPDFUrl: SafeResourceUrl | null = null;
 25	 currentPDFName = '';
 26	
 27	 constructor(
 28	 private route: ActivatedRoute,
 29	 private router: Router,
 30	 private employeeService: EmployeeService,
 31	 private sanitizer: DomSanitizer
 32	 ) {}
 33	
 34	 ngOnInit() {
 35	 this.route.params.subscribe(params => {
 36	 this.matricule = params['matricule'];
 37	 if (this.matricule) {
 38	 this.loadEmployeeData();
 39	 }
 40	 });
 41	 }
 42	
 43	 loadEmployeeData() {
 44	 this.loading = true;
 45	 this.error = '';
 46	
 47	 this.employeeService.getEmployeeByMatricule(this.matricule).subscribe({
 48	 next: (employee) => {
 49	 this.employee = employee;
 50	 if (employee.id) {
 51	 this.loadApplications(employee.id);
 52	 }
 53	 },
 54	 error: (error) => {
 55	 this.error = 'Employee not found. Please check your matricule number.';
 56	 this.loading = false;
 57	 }
 58	 });
 59	 }
 60	
 61	 loadApplications(employeeId: number) {
 62	 this.employeeService.getEmployeeApplications(employeeId).subscribe({
 63	 next: (applications) => {
 64	 this.applications = applications;
 65	 this.loading = false;
 66	 },
 67	 error: (error) => {
 68	 this.error = 'Error loading applications. Please try again.';
 69	 this.loading = false;
 70	 }
 71	 });
 72	 }
 73	
 74	 // PDF viewing methods
 75	 viewPDF(pdfPath: string, pdfName: string) {
 76	 // Construct the PDF URL - assuming the backend serves files at /api/files/
 77	 const pdfUrl = `http://localhost:8080/api/files/${pdfName}`;
 78	 this.currentPDFUrl = this.sanitizer.bypassSecurityTrustResourceUrl(pdfUrl);
 79	 this.currentPDFName = pdfName;
 80	 this.showPDFModal = true;
 81	 }
 82	
 83	 closePDFModal() {
 84	 this.showPDFModal = false;
 85	 this.currentPDFUrl = null;
 86	 this.currentPDFName = '';
 87	 }
 88	
 89	 getStatusClass(status: ApplicationStatus): string {
 90	 switch (status) {
 91	 case ApplicationStatus.EN_ATTENTE:
 92	 return 'status-pending';
 93	 case ApplicationStatus.ACCEPTED:
 94	 return 'status-accepted';
 95	 case ApplicationStatus.REJECTED:
 96	 return 'status-rejected';
 97	 default:
 98	 return '';
 99	 }
 100	 }
...
 141	
 142	 formatDateTime(dateString: string): string {
 143	 if (!dateString) return '';
 144	 try {
 145	 const date = new Date(dateString);
 146	 return date.toLocaleDateString('en-US', {
 147	 year: 'numeric',
 148	 month: 'short',
 149	 day: 'numeric',
 150	 hour: '2-digit',
 151	 minute: '2-digit'
 152	 });
 153	 } catch (error) {
 154	 return dateString;
 155	 }
 156	 }
 157	
 158	 goToEmployeeForm() {
 159	 this.router.navigate(['/employee-form']);
 160	 }
 161	
 162	 goToAdminDashboard() {
 163	 this.router.navigate(['/admin-dashboard']);
 164	 }
 165	
 166	 refresh() {
 167	 this.loadEmployeeData();
 168	 }
 169	
 170	 getTotalChildren(): number {
 171	 if (!this.employee) return 0;
 172	 
 173	 const primary = this.employee.primaryEducationChildren?.length || 0;
 174	 const secondary = this.employee.secondaryEducationChildren?.length || 0;
 175	 const higher = this.employee.higherEducationChildren?.length || 0;
 176	 
 177	 return primary + secondary + higher;
 178	 }
 179	
 180	 hasSpouseInfo(): boolean {
 181	 return !!(this.employee?.wifeFirstName || this.employee?.wifeLastName || this.employee?.wifeWorkplace || this.employee?.wifeMatricule);
 182	 }
 183	
 184	 getApplicationStatusSummary(): string {
 185	 if (this.applications.length === 0) return 'No applications submitted';
 186	 
 187	 const pending = this.applications.filter(app => app.status === ApplicationStatus.EN_ATTENTE).length;
 188	 const accepted = this.applications.filter(app => app.status === ApplicationStatus.ACCEPTED).length;
 189	 const rejected = this.applications.filter(app => app.status === ApplicationStatus.REJECTED).length;
 190	 
 191	 if (pending > 0) return `${pending} application(s) pending review`;
 192	 if (accepted > 0 && rejected === 0) return `${accepted} application(s) approved`;
 193	 if (rejected > 0 && accepted === 0) return `${rejected} application(s) rejected`;
 194	 return `${accepted} approved, ${rejected} rejected`;
 195	 }
 196	}
