 4	import org.springframework.beans.factory.annotation.Autowired;
 5	import org.springframework.core.io.Resource;
 6	import org.springframework.http.HttpHeaders;
 7	import org.springframework.http.MediaType;
 8	import org.springframework.http.ResponseEntity;
 9	import org.springframework.web.bind.annotation.*;
 10	import org.springframework.web.multipart.MultipartFile;
 11	
 12	import jakarta.servlet.http.HttpServletRequest;
 13	import java.io.IOException;
 14	import java.util.HashMap;
 15	import java.util.Map;
 16	
 17	@RestController
 18	@RequestMapping("/api/files")
 19	@CrossOrigin(origins = "*")
 20	public class FileController {
 21	
 22	 @Autowired
 23	 private FileStorageService fileStorageService;
 24	
 25	 @PostMapping("/upload")
 26	 public ResponseEntity<?> uploadFile(
 27	 @RequestParam("file") MultipartFile file,
 28	 @RequestParam(value = "category", defaultValue = "general") String category) {
 29	 try {
 30	 String fileName = fileStorageService.storeFile(file, category);
 31	 
 32	 Map<String, String> response = new HashMap<>();
 33	 response.put("fileName", fileName);
 34	 response.put("message", "File uploaded successfully");
 35	 response.put("downloadUri", "/api/files/" + fileName);
 36	 
 37	 return ResponseEntity.ok(response);
 38	 } catch (Exception e) {
 39	 Map<String, String> errorResponse = new HashMap<>();
 40	 errorResponse.put("error", "Could not upload file: " + e.getMessage());
 41	 return ResponseEntity.badRequest().body(errorResponse);
 42	 }
 43	 }
 44	
 45	 @GetMapping("/{fileName:.+}")
 46	 public ResponseEntity<Resource> downloadFile(
 47	 @PathVariable String fileName, 
 48	 HttpServletRequest request) {
 49	 try {
 50	 // Load file as Resource
 51	 Resource resource = fileStorageService.loadFileAsResource(fileName);
 52	
 53	 // Try to determine file's content type
 54	 String contentType = null;
 55	 try {
 56	 contentType = request.getServletContext().getMimeType(resource.getFile().getAbsolutePath());
 57	 } catch (IOException ex) {
 58	 // Fallback to default content type
 59	 contentType = "application/octet-stream";
 60	 }
 61	
 62	 // If content type could not be determined, set default
 63	 if (contentType == null) {
 64	 if (fileName.toLowerCase().endsWith(".pdf")) {
 65	 contentType = "application/pdf";
 66	 } else {
 67	 contentType = "application/octet-stream";
 68	 }
 69	 }
 70	
 71	 return ResponseEntity.ok()
 72	 .contentType(MediaType.parseMediaType(contentType))
 73	 .header(HttpHeaders.CONTENT_DISPOSITION, "inline; filename=\"" + resource.getFilename() + "\"")
 74	 .body(resource);
 75	 } catch (Exception e) {
 76	 return ResponseEntity.notFound().build();
 77	 }
 78	 }
 79	
 80	 @GetMapping("/view/{fileName:.+}")
 81	 public ResponseEntity<Resource> viewFile(
 82	 @PathVariable String fileName, 
 83	 HttpServletRequest request) {
 84	 try {
 85	 // Load file as Resource
 86	 Resource resource = fileStorageService.loadFileAsResource(fileName);
 87	
 88	 // Determine content type for viewing
 89	 String contentType = "application/pdf";
 90	 if (fileName.toLowerCase().endsWith(".pdf")) {
 91	 contentType = "application/pdf";
 92	 } else if (fileName.toLowerCase().endsWith(".jpg") || fileName.toLowerCase().endsWith(".jpeg")) {
 93	 contentType = "image/jpeg";
 94	 } else if (fileName.toLowerCase().endsWith(".png")) {
 95	 contentType = "image/png";
 96	 } else {
 97	 contentType = "application/octet-stream";
 98	 }
 99	
 100	 return ResponseEntity.ok()
 101	 .contentType(MediaType.parseMediaType(contentType))
 102	 .header(HttpHeaders.CONTENT_DISPOSITION, "inline; filename=\"" + resource.getFilename() + "\"")
 103	 .header(HttpHeaders.CACHE_CONTROL, "no-cache, no-store, must-revalidate")
 104	 .header(HttpHeaders.PRAGMA, "no-cache")
 105	 .header(HttpHeaders.EXPIRES, "0")
 106	 .body(resource);
 107	 } catch (Exception e) {
 108	 return ResponseEntity.notFound().build();
 109	 }
 110	 }
 111	
 112	 @DeleteMapping("/{fileName:.+}")
 113	 public ResponseEntity<?> deleteFile(@PathVariable String fileName) {
 114	 try {
 115	 boolean deleted = fileStorageService.deleteFileByName(fileName);
 116	 if (deleted) {
 117	 Map<String, String> response = new HashMap<>();
 118	 response.put("message", "File deleted successfully");
 119	 return ResponseEntity.ok(response);
 120	 } else {
 121	 Map<String, String> errorResponse = new HashMap<>();
 122	 errorResponse.put("error", "File not found");
 123	 return ResponseEntity.notFound().build();
 124	 }
 125	 } catch (Exception e) {
 126	 Map<String, String> errorResponse = new HashMap<>();
 127	 errorResponse.put("error", "Could not delete file: " + e.getMessage());
 128	 return ResponseEntity.badRequest().body(errorResponse);
 129	 }
 130	 }
 131	
 132	 @GetMapping("/info/{fileName:.+}")
 133	 public ResponseEntity<?> getFileInfo(@PathVariable String fileName) {
 134	 try {
 135	 Resource resource = fileStorageService.loadFileAsResource(fileName);
 136	 
 137	 Map<String, Object> fileInfo = new HashMap<>();
 138	 fileInfo.put("fileName", resource.getFilename());
 139	 fileInfo.put("size", resource.getFile().length());
 140	 fileInfo.put("lastModified", resource.getFile().lastModified());
 141	 fileInfo.put("exists", resource.exists());
