 25	 public FileStorageServiceImpl(@Value("${file.upload-dir:uploads}") String uploadDir) {
 26	 this.uploadDir = uploadDir;
 27	 this.fileStorageLocation = Paths.get(uploadDir).toAbsolutePath().normalize();
 28	 
 29	 try {
 30	 Files.createDirectories(this.fileStorageLocation);
 31	 } catch (Exception ex) {
 32	 throw new RuntimeException("Could not create the directory where the uploaded files will be stored.", ex);
 33	 }
 34	 }
 35	
 36	 @Override
 37	 public String storeFile(MultipartFile file, String directory) throws IOException {
 38	 // Create directory if it doesn't exist
 39	 Path uploadPath = Paths.get(uploadDir, directory);
 40	 if (!Files.exists(uploadPath)) {
 41	 Files.createDirectories(uploadPath);
 42	 }
 43	
 44	 // Generate unique filename
 45	 String originalFilename = file.getOriginalFilename();
 46	 String fileExtension = "";
 47	 if (originalFilename != null && originalFilename.contains(".")) {
 48	 fileExtension = originalFilename.substring(originalFilename.lastIndexOf("."));
 49	 }
 50	 String uniqueFilename = UUID.randomUUID().toString() + fileExtension;
 51	
 52	 // Store file
 53	 Path filePath = uploadPath.resolve(uniqueFilename);
 54	 Files.copy(file.getInputStream(), filePath, StandardCopyOption.REPLACE_EXISTING);
 55	
 56	 return uniqueFilename; // Return just the filename for easier access
 57	 }
 58	
 59	 @Override
 60	 public byte[] loadFile(String filePath) throws IOException {
 61	 Path path = Paths.get(filePath);
 62	 return Files.readAllBytes(path);
 63	 }
 64	
 65	 @Override
 66	 public Resource loadFileAsResource(String fileName) throws IOException {
 67	 try {
 68	 // First try to find the file in the main upload directory
 69	 Path filePath = this.fileStorageLocation.resolve(fileName).normalize();
 70	 Resource resource = new UrlResource(filePath.toUri());
 71	 
 72	 if (resource.exists()) {
 73	 return resource;
 74	 }
 75	 
 76	 // If not found, search in subdirectories
 77	 String[] subdirectories = {"primary-education", "secondary-education", "higher-education", "general"};
 78	 for (String subdir : subdirectories) {
 79	 filePath = this.fileStorageLocation.resolve(subdir).resolve(fileName).normalize();
 80	 resource = new UrlResource(filePath.toUri());
 81	 if (resource.exists()) {
 82	 return resource;
 83	 }
 84	 }
 85	 
 86	 throw new IOException("File not found: " + fileName);
 87	 } catch (MalformedURLException ex) {
 88	 throw new IOException("File not found: " + fileName, ex);
 89	 }
 90	 }
 91	
 92	 @Override
 93	 public void deleteFile(String filePath) throws IOException {
 94	 Path path = Paths.get(filePath);
 95	 Files.deleteIfExists(path);
 96	 }
 97	
 98	 @Override
 99	 public boolean deleteFileByName(String fileName) {
 100	 try {
 101	 // First try to find and delete the file in the main upload directory
 102	 Path filePath = this.fileStorageLocation.resolve(fileName).normalize();
 103	 if (Files.exists(filePath)) {
 104	 Files.delete(filePath);
 105	 return true;
 106	 }
 107	 
 108	 // If not found, search in subdirectories
 109	 String[] subdirectories = {"primary-education", "secondary-education", "higher-education", "general"};
 110	 for (String subdir : subdirectories) {
 111	 filePath = this.fileStorageLocation.resolve(subdir).resolve(fileName).normalize();
 112	 if (Files.exists(filePath)) {
 113	 Files.delete(filePath);
 114	 return true;
 115	 }
 116	 }
 117	 
 118	 return false; // File not found
 119	 } catch (IOException ex) {
 120	 return false;
 121	 }
 122	 }
 123	
 124	 @Override
 125	 public boolean fileExists(String filePath) {
 126	 Path path = Paths.get(filePath);
 127	 return Files.exists(path);
 128	 }
 129	}
