 3	import com.fasterxml.jackson.annotation.JsonBackReference;
 4	import jakarta.persistence.*;
 5	import java.time.LocalDateTime;
 6	
 7	@Entity
 8	@Table(name = "applications")
 9	public class Application {
 10	 
 11	 @Id
 12	 @GeneratedValue(strategy = GenerationType.IDENTITY)
 13	 private Long id;
 14	 
 15	 @ManyToOne(fetch = FetchType.LAZY)
 16	 @JoinColumn(name = "employee_id")
 17	 @JsonBackReference("employee-applications")
 18	 private Employee employee;
 19	 
