 11	@Entity
 12	@Table(name = "employees")
 13	public class Employee {
 14	 
 15	 @Id
 16	 @GeneratedValue(strategy = GenerationType.IDENTITY)
 17	 private Long id;
 18	 
 19	 @NotBlank(message = "First name is required")
